import axios from 'axios'; // Import axios
import React, { useState, useEffect } from 'react';
import './css/expense-form.css'; // In ExpenseForm.js


function ExpenseForm(props) {
    const [description, setDescription] = useState('');
    const [amount, setAmount] = useState(0);

    const handleSubmit = async (event) => {
        event.preventDefault();

        const expense = {
            description,
            amount,
            // Set the user ID based on your authentication logic
            userId: props.user_id, // Your logged-in user ID
        };

        try {
            const response = await axios.post('localhost:3000', expense);
            console.log('Expense added successfully:', response.data);
            // Clear form after successful submission
            setDescription('');
            setAmount(0);
            // Handle success (e.g., display feedback, redirect)
        } catch (error) {
            console.error('Error adding expense:', error);
            // Handle error (e.g., display error message)
        }
    };

    return (
        <div className="expense-form">
            <h1>Add Expense</h1>
            <form onSubmit={handleSubmit}>
                <label htmlFor="description">Description:</label>
                <input
                    type="text"
                    id="description"
                    value={description}
                    onChange={(e) => setDescription(e.target.value)}
                />
                <label htmlFor="amount">Amount:</label>
                <input
                    type="number"
                    id="amount"
                    value={amount}
                    onChange={(e) => setAmount(e.target.value)}
                />
                <button type="submit">Submit</button>
            </form>
        </div>
    );
}

export default ExpenseForm;
